export interface ChainInfo {
    id: ChainId;
    name: ChainName;
    chainType: ChainType;
    icon: string;
    nativeIcon?: string;
    fullname: string;
    network: string;
    website: string;
    nativeCurrency: NativeCurrency;
    rpcUrl: string;
    blockExplorerUrl: string;
    features?: Feature[];
    faucetUrl?: string;
}
export interface NativeCurrency {
    name: string;
    symbol: string;
    decimals: number;
}
export interface Feature {
    name: string;
    contracts?: {
        name: string;
        version: string;
    }[];
}
export type ChainId = number;
export type ChainName = string;
export interface Chain {
    id: ChainId;
    name: ChainName;
}
export type ChainType = 'evm' | 'solana';
